import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { Shield, Mail, CheckCircle, AlertCircle, ArrowRight, RefreshCw } from "lucide-react";

// Existing user login functionality moved to separate sign-in page

export default function Setup() {
  const [, setLocation] = useLocation();
  const [userEmail, setUserEmail] = useState("");
  const [authInProgress, setAuthInProgress] = useState(false);
  const { toast } = useToast();
  const { login, isAuthenticated } = useAuth();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      setLocation("/");
    }
  }, [isAuthenticated, setLocation]);

  // Check for OAuth callback code in URL
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    const email = localStorage.getItem('pending_auth_email');

    if (code && email) {
      handleGmailCallback.mutate({ code, email });
      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
      localStorage.removeItem('pending_auth_email');
    }
  }, []);

  const handleGmailCallback = useMutation({
    mutationFn: async ({ code, email }: { code: string; email: string }) => {
      const res = await apiRequest("POST", "/api/auth/gmail/callback", { code, email });
      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Authentication failed');
      }
      return res.json();
    },
    onSuccess: (data) => {
      login(data.user);
      toast({
        title: "Gmail Connected!",
        description: "Your email filtering system is now active.",
      });
      setLocation("/");
    },
    onError: (error: any) => {
      console.error('Gmail callback error:', error);
      toast({
        title: "Authentication Failed",
        description: error.message || "Failed to connect Gmail account",
        variant: "destructive",
      });
      setAuthInProgress(false);
    }
  });

  const getGmailAuthUrl = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("GET", "/api/auth/gmail");
      return res.json();
    },
    onSuccess: (data) => {
      localStorage.setItem('pending_auth_email', userEmail);
      window.location.href = data.authUrl;
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to initialize Gmail authentication",
        variant: "destructive",
      });
      setAuthInProgress(false);
    }
  });

  const handleGmailAuth = () => {
    if (!userEmail.trim()) {
      toast({
        title: "Email Required",
        description: "Please enter your Gmail address",
        variant: "destructive",
      });
      return;
    }

    setAuthInProgress(true);
    getGmailAuthUrl.mutate();
  };

  const isValidEmail = (email: string) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-primary rounded-2xl flex items-center justify-center mx-auto mb-4">
            <Shield className="text-white" size={32} />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome to Email Guardian</h1>
          <p className="text-gray-600 text-lg">
            Set up your donation-based email filtering system in just a few steps
          </p>
        </div>

        {/* Setup Steps */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Mail className="text-blue-600" size={24} />
              <span>Connect Your Gmail Account</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <Alert>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                Email Guardian needs access to your Gmail account to filter incoming emails. 
                We only read emails and manage labels - we never delete or modify your email content.
              </AlertDescription>
            </Alert>

            <div className="space-y-4">
              <div>
                <Label htmlFor="email" className="text-base font-medium">Your Gmail Address</Label>
                <Input
                  id="email"
                  type="email"
                  value={userEmail}
                  onChange={(e) => setUserEmail(e.target.value)}
                  placeholder="your.email@gmail.com"
                  className="mt-2"
                  disabled={authInProgress}
                />
              </div>

              <Button 
                onClick={handleGmailAuth}
                disabled={!isValidEmail(userEmail) || authInProgress || handleGmailCallback.isPending}
                className="w-full bg-primary hover:bg-blue-700 text-white py-3"
                size="lg"
              >
                {authInProgress || handleGmailCallback.isPending ? (
                  <>
                    <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
                    {handleGmailCallback.isPending ? "Completing Setup..." : "Connecting to Gmail..."}
                  </>
                ) : (
                  <>
                    <Mail className="mr-2" size={16} />
                    Connect Gmail Account
                    <ArrowRight className="ml-2" size={16} />
                  </>
                )}
              </Button>

              <div className="text-center mt-4 space-y-2">
                <Button 
                  variant="outline"
                  onClick={() => setLocation("/demo")}
                  className="text-sm w-full"
                >
                  Try Demo Mode Instead
                </Button>

                <div className="text-center">
                  <p className="text-sm text-gray-600">
                    Already have an account?{" "}
                    <Button
                      variant="link"
                      className="p-0 h-auto text-blue-600 hover:text-blue-800"
                      onClick={() => setLocation("/signin")}
                      data-testid="link-signin"
                    >
                      Sign in here
                    </Button>
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Features Preview */}
        <Card>
          <CardHeader>
            <CardTitle>What happens next?</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="text-center p-4">
                <div className="w-12 h-12 bg-blue-50 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <Shield className="text-blue-600" size={24} />
                </div>
                <h3 className="font-medium text-gray-900 mb-2">Email Filtering</h3>
                <p className="text-sm text-gray-500">
                  Unknown senders will be filtered and prompted to donate $1
                </p>
              </div>

              <div className="text-center p-4">
                <div className="w-12 h-12 bg-green-50 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <CheckCircle className="text-green-600" size={24} />
                </div>
                <h3 className="font-medium text-gray-900 mb-2">Automatic Processing</h3>
                <p className="text-sm text-gray-500">
                  Paid emails are automatically moved to your inbox
                </p>
              </div>

              <div className="text-center p-4">
                <div className="w-12 h-12 bg-purple-50 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <Mail className="text-purple-600" size={24} />
                </div>
                <h3 className="font-medium text-gray-900 mb-2">Contact Management</h3>
                <p className="text-sm text-gray-500">
                  Known contacts bypass the filter completely
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Footer */}
        <div className="text-center mt-8 text-sm text-gray-500">
          <p>Your data is secure and encrypted. We follow Gmail's security best practices.</p>
        </div>
      </div>
    </div>
  );
}

client/src/components/layout/header.tsx.content:
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { RefreshCw, LogOut } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";

interface HeaderProps {
  title: string;
  subtitle: string;
  gmailStatus: "connected" | "disconnected";
}

const CURRENT_USER_ID = "user-123";

export function Header({ title, subtitle, gmailStatus }: HeaderProps) {
  const { toast } = useToast();
  const { user, logout } = useAuth();
  const [, setLocation] = useLocation();

  const syncMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", "/api/process-emails", { userId: user?.id });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/pending-emails"] });
      queryClient.invalidateQueries({ queryKey: ["/api/donations"] });
      toast({
        title: "Sync Complete",
        description: "Successfully processed new emails and updated stats.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Sync Failed",
        description: error.message || "Failed to sync emails",
        variant: "destructive",
      });
    }
  });

  return (
    <header className="bg-white border-b border-gray-200 px-8 py-4">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
          <p className="text-gray-600 mt-1">{subtitle}</p>
        </div>
        <div className="flex items-center space-x-4">
          <Badge 
            variant={gmailStatus === "connected" ? "default" : "destructive"}
            className={gmailStatus === "connected" ? "bg-green-100 text-green-700 border-green-200" : ""}
          >
            <div className={`w-2 h-2 rounded-full mr-2 ${gmailStatus === "connected" ? "bg-green-500" : "bg-red-500"}`}></div>
            Gmail {gmailStatus === "connected" ? "Connected" : "Disconnected"}
          </Badge>
          <Button 
            onClick={() => syncMutation.mutate()}
            disabled={syncMutation.isPending}
            className="bg-primary hover:bg-blue-700"
          >
            <RefreshCw className={`mr-2 ${syncMutation.isPending ? "animate-spin" : ""}`} size={16} />
            {syncMutation.isPending ? "Syncing..." : "Sync Now"}
          </Button>
          <Button 
            variant="outline"
            onClick={() => {
              logout();
              setLocation("/setup");
            }}
            className="text-gray-600 hover:text-gray-800"
          >
            <LogOut className="mr-2" size={16} />
            Logout
          </Button>
        </div>
      </div>
    </header>
  );
}

client/src/components/layout/sidebar.tsx.content:
import { Link, useLocation } from "wouter";
import { cn } from "@/lib/utils";
import { 
  Shield, 
  LayoutDashboard, 
  Inbox, 
  Users, 
  DollarSign, 
  Settings 
} from "lucide-react";

const navigation = [
  { name: "Dashboard", href: "/", icon: LayoutDashboard },
  { name: "Email Queue", href: "/email-queue", icon: Inbox },
  { name: "Known Contacts", href: "/contacts", icon: Users },
  { name: "Donations", href: "/donations", icon: DollarSign },
  { name: "Settings", href: "/settings", icon: Settings },
];

export function Sidebar() {
  const [location] = useLocation();

  return (
    <aside className="w-64 bg-white shadow-sm border-r border-gray-200 fixed h-full">
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
            <Shield className="text-white" size={20} />
          </div>
          <div>
            <h1 className="font-bold text-lg text-gray-900">Email Guardian</h1>
            <p className="text-sm text-gray-500">Gmail Filter System</p>
          </div>
        </div>
      </div>

      <nav className="p-4">
        <ul className="space-y-2">
          {navigation.map((item) => {
            const isActive = location === item.href;
            return (
              <li key={item.name}>
                <Link href={item.href}>
                  <span className={cn(
                    "flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors cursor-pointer",
                    isActive 
                      ? "text-gray-700 bg-blue-50 border-l-4 border-primary font-medium" 
                      : "text-gray-600 hover:bg-gray-50"
                  )}>
                    <item.icon size={20} />
                    <span>{item.name}</span>
                  </span>
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
    </aside>
  );
}

client/src/components/ui/alert-dialog.ts.content:
import * as React from "react"
import * as AlertDialogPrimitive from "@radix-ui/react-alert-dialog"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

const AlertDialog = AlertDialogPrimitive.Root

const AlertDialogTrigger = AlertDialogPrimitive.Trigger

const AlertDialogPortal = AlertDialogPrimitive.Portal

const AlertDialogOverlay = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName

const AlertDialogContent = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
